import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    },
  },
  button: {
    margin: theme.spacing(1),
  }

}))



function Form() {


  const classes = useStyles()
  const [inputFields, setInputFields] = useState([
    { date: '', startTime: '' , volunteer_userID: '', "beneficiary_beneficiaryID": ''},
  ]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // prevent page reload everytime form submission is done
    console.log("InputFields", inputFields);
  };

  const handleChangeInput = (index, event) => {
    const values = [...inputFields];
    values[index][event.target.name] = event.target.value;
    setInputFields(values);
  }

  const handleAddFields = () => {
    setInputFields([...inputFields, { firstName: '', lastName: '', volunteer_userID: '', beneficiary_beneficiaryID:'' }])
  }

  const handleRemoveFields = (index) => {
    const values  = [...inputFields];
    values.splice(index, 1);
    setInputFields(values);
  }
  return (
    <Container>

      <form className = {classes.root}>
      {inputFields.map((inputField, index) => (
          <div key={index}>
            <TextField 
              name="date"
              label="Date"
              variant="filled"
              value={inputField.date}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="startTime"
              label="Start Time"
              variant="filled"
              value={inputField.startTime}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="volunteer_userID"
              label="Volunteer ID"
              variant="filled"
              value={inputField.volunteer_userID}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="beneficiary_beneficiaryID"
              label="Beneficiary ID"
              variant="filled"
              value={inputField.beneficiary_beneficiaryID}
              onChange={event => handleChangeInput(index, event)}
            />
            <IconButton
              onClick={() => handleRemoveFields(index)}
            >
              <RemoveIcon />
            </IconButton>
            <IconButton
              onClick={() => handleAddFields()}
            >
              <AddIcon />
            </IconButton>
          </div>
      ))}
      <Button 
        className={classes.button}
        variant="contained" 
        color="primary" 
        type="submit" 
        endIcon={<Icon>Post</Icon>}
        onClick={handleSubmit}
      >Send</Button>

      </form>


    </Container>
  );
}

export default Form;
