import React, { useState, Component } from 'react';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    },
  },
  button: {
    margin: theme.spacing(1),
  }

}))

// class Postt extends Component{
//   async postData(e) {
//     try{
//       e.preventDefault();
//     // prevent page reload everytime form submission is done
//       let result = await fetch( "https://cors-anywhere.herokuapp.com/https://smucf-dev-ebs-g1t3-srv.cfapps.us10.hana.ondemand.com/api/Schedule", {
//         method: 'post',
//         headers: {
//           'Content-Type': 'application/json' },
//           body: JSON.stringify(e)
//         });

//     } catch(e){
//       console.log(e)
//     }
//   }
// }


function Form() {


  const classes = useStyles()
  const [inputFields, setInputFields] = useState([
    { 
    date: '', 
    startTime: '' , 
    charity_charityID:'', 
    Details: [
      {
        deliverStatus: "NotCompleted", 
        parent_scheduleID: '', 
        volunteer_userID: '', 
        beneficiary_beneficiaryID: '',
        Food_BasketID: ''
      }
    ]
  },

]  
);
  // const [inputFields, setInputFields] = useState([
  //   { date: '', 
  //     startTime: '' , charity_charityID:'', 
  //     deliverStatus: "NotCompleted", 
  //     parent_scheduleID: '', 
  //     volunteer_userID: '', 
  //     beneficiary_beneficiaryID: '',
  //     Food_BasketID: ""
  //   },
  // ]);


  const hardcode = { 
    "date" : "2020-11-08",
    "startTime": "9:00 AM",
    "charity_charityID" : "charity001",
    "Details": [
        {
            "deliverStatus": "NotCompleted",
            "parent_scheduleID": "newtest",
            "volunteer_userID" : "user001",
            "beneficiary_beneficiaryID" : "bene001",
            "Food_BasketID": "01"
        }
    ]
  }
  // async function posttoreview(e) {
  //   const postres = await fetch(
  //     "https://cors-anywhere.herokuapp.com/https://smucf-dev-ebs-g1t3-srv.cfapps.us10.hana.ondemand.com/api/Schedule",
  //     {
  //       method: 'POST', // or 'PUT'
  //       headers: { 'Content-Type': 'application/json' },
  //       body: JSON.stringify(e),
  //     }
  //   );
  //   const postresult = await postres.json();
  //   return postresult;
  // }
  async function handleSubmit(e){
    e.preventDefault();
    // prevent page reload everytime form submission is done
    console.log("InputFields", inputFields);

    const result = await fetch( "https://cors-anywhere.herokuapp.com/https://smucf-dev-ebs-g1t3-srv.cfapps.us10.hana.ondemand.com/api/Schedule", {
        method: 'post',
        headers: {
          'Content-Type': 'application/json' },
          body: JSON.stringify(inputFields),
        });

    // } catch(e){
    //   console.log(e)
    // }
  };

  const handleChangeInput = (index, event) => {
    const values = [...inputFields];
    values[index][event.target.name] = event.target.value;
    setInputFields(values);
  }

  const handleAddFields = () => {
    setInputFields([...inputFields, 
      // { firstName: '', 
      // lastName: '', 
      // volunteer_userID: '', 
      // beneficiary_beneficiaryID:'' 
      // },
      {date: '', startTime: '' , charity_charityID:'', Details: 
      [
        {deliverStatus: "NotCompleted", 
        parent_scheduleID: '', 
        volunteer_userID: '', 
        beneficiary_beneficiaryID: '',
        Food_BasketID: ""
        }
      ]}
    ])
  }
//   const handleAddFields = () => {
//     setInputFields([...inputFields, 
//       {date: '', 
          // startTime: '' , 
          // charity_charityID:'', 
          // deliverStatus: "NotCompleted", 
//         parent_scheduleID: '', 
//         volunteer_userID: '', 
//         beneficiary_beneficiaryID: '',
//         Food_BasketID: ""
//         }
//       ]}
//     ])
//   }
  const handleRemoveFields = (index) => {
    const values  = [...inputFields];
    values.splice(index, 1);
    setInputFields(values);
  }
  return (
    <Container>

      <form className = {classes.root}>
      {inputFields.map((inputField, index) => (
          <div key={index}>
            <TextField 
              name="date"
              label="Date"
              variant="filled"
              value={inputField.date}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="startTime"
              label="Start Time"
              variant="filled"
              value={inputField.startTime}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="charity_charityID"
              label="Charity ID"
              variant="filled"
              value={inputField.charity_charityID}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="parent_scheduleID"
              label="Schedule ID"
              variant="filled"
              value={inputField.parent_scheduleID}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="volunteer_userID"
              label="Volunteer ID"
              variant="filled"
              value={inputField.volunteer_userID}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="beneficiary_beneficiaryID"
              label="Beneficiary ID"
              variant="filled"
              value={inputField.beneficiary_beneficiaryID}
              onChange={event => handleChangeInput(index, event)}
            />
            <TextField 
              name="Food_BasketID"
              label="Food ID"
              variant="filled"
              value={inputField.Food_BasketID}
              onChange={event => handleChangeInput(index, event)}
            />

            <IconButton
              onClick={() => handleRemoveFields(index)}
            >
              <RemoveIcon />
            </IconButton>
            <IconButton
              onClick={() => handleAddFields()}
            >
              <AddIcon />
            </IconButton>
          </div>
      ))}
      <Button 
        className={classes.button}
        variant="contained" 
        color="primary" 
        type="submit" 
        // endIcon={<Icon>send</Icon>}
        onClick={handleSubmit}
        // onClick={()=> this.postData(hardcode)}
      >Post</Button>

      </form>


    </Container>
  );
}

export default Form;
