function Form() {


  const classes = useStyles()
  // const [inputFieldsdate, setInputFieldsdate] = useState(null)

  const [inputFieldsStartTime, setInputFieldsStartTime] = useState(null)
  const [inputFieldsCharity_charityID, setInputFieldscharity_charityID] = useState(null)
  const [inputFieldsParent_scheduleID, setInputFieldsParent_scheduleID] = useState(null)
  const [inputFieldsVolunteer_userID, setInputFieldsVolunteer_userID] = useState(null)
  const [inputFieldsBeneficiary_beneficiaryID, setInputFieldsBeneficiary_beneficiaryID] = useState(null)
  const [inputFieldsFood_BasketID, setInputFieldsFood_BasketID] = useState(null)


  // const [inputFields, setInputFields] = useState([
  //   { "date": '', 
  //   startTime: '' , 
  //   charity_charityID:'', 
  //   parent_scheduleID: '', 
  //   // deliverStatus: "NotCompleted", 
  //   volunteer_userID: '', 
  //   beneficiary_beneficiaryID: '',
  //   Food_BasketID: ''
  //   },
  // ]);
  // const [inputFields, setInputFields] = useState([
  //   { date: '', 
  //     startTime: '' , charity_charityID:'', 
  //     deliverStatus: "NotCompleted", 
  //     parent_scheduleID: '', 
  //     volunteer_userID: '', 
  //     beneficiary_beneficiaryID: '',
  //     Food_BasketID: ""
  //   },
  // ]);


  // const hardcode = { 
  //   "date" : "2020-11-08",
  //   "startTime": "9:00 AM",
  //   "charity_charityID" : "charity001",
  //   "Details": [
  //       {
  //           "deliverStatus": "NotCompleted",
  //           "parent_scheduleID": "newtest",
  //           "volunteer_userID" : "user001",
  //           "beneficiary_beneficiaryID" : "bene001",
  //           "Food_BasketID": "01"
  //       }
  //   ]
  // }
  // async function posttoreview(e) {
  //   const postres = await fetch(
  //     "https://cors-anywhere.herokuapp.com/https://smucf-dev-ebs-g1t3-srv.cfapps.us10.hana.ondemand.com/api/Schedule",
  //     {
  //       method: 'POST', // or 'PUT'
  //       headers: { 'Content-Type': 'application/json' },
  //       body: JSON.stringify(e),
  //     }
  //   );
  //   const postresult = await postres.json();
  //   return postresult;
  // }
  async function handleSubmit(e){
    e.preventDefault();
    // prevent page reload everytime form submission is done
    console.log("InputFields", inputFields);
    console.log("tst", inputFields.date);
    const result = await fetch( "https://cors-anywhere.herokuapp.com/https://smucf-dev-ebs-g1t3-srv.cfapps.us10.hana.ondemand.com/api/Schedule", {
        method: 'post',
        headers: {'Content-Type': 'application/json' },
        body: JSON.stringify({
              "date" : inputFieldsdate,
              "startTime": inputFieldsTime,
              "charity_charityID" : "charity001",
              "Details": [
                  {
                  "deliverStatus": "NotCompleted",
                  "parent_scheduleID": "newtest",
                  "volunteer_userID" : "user001",
                  "beneficiary_beneficiaryID" : "bene001",
                  "Food_BasketID": "01"
                  }]
        })
      })
    // } catch(e){
    //   console.log(e)
    // }
    }

  const handleChangeInput = (index, event) => {
    // const values = [...inputFields];
    // values[index][event.target.name] = event.target.value;
    // setInputFields(values);
    if (event.target.id == 'date') setInputFieldsdate(event.target.value);
    if (event.target.id == 'date') setInputFieldsStartTime(event.target.value);

    if (event.target.id == 'date') setInputFieldscharity_charityID(event.target.value);

    if (event.target.id == 'date') setInputFieldsParent_scheduleID(event.target.value);

    if (event.target.id == 'date') setInputFieldsVolunteer_userID(event.target.value);

    if (event.target.id == 'date') setInputFieldsdate(event.target.value);

    if (event.target.id == 'date') setInputFieldsdate(event.target.value);

    if (event.target.id == 'date') setInputFieldsdate(event.target.value);


  const [inputFieldsStartTime, setInputFieldsStartTime] = useState(null)
  const [inputFieldsCharity_charityID, setInputFieldscharity_charityID] = useState(null)
  const [inputFieldsParent_scheduleID, setInputFieldsParent_scheduleID] = useState(null)
  const [inputFieldsVolunteer_userID, setInputFieldsVolunteer_userID] = useState(null)
  const [inputFieldsBeneficiary_beneficiaryID, setInputFieldsBeneficiary_beneficiaryID] = useState(null)
  const [inputFieldsFood_BasketID, setInputFieldsFood_BasketID] = useState(null)
  }

  const handleAddFields = () => {
    setInputFields([...inputFields, 
      // { firstName: '', 
      // lastName: '', 
      // volunteer_userID: '', 
      // beneficiary_beneficiaryID:'' 
      // },
      {date: '', startTime: '' , charity_charityID:'', Details: 
      [
        {deliverStatus: "NotCompleted", 
        parent_scheduleID: '', 
        volunteer_userID: '', 
        beneficiary_beneficiaryID: '',
        Food_BasketID: ""
        }
      ]}
    ])
  }
  // const handleAddFields = () => {
  //   setInputFields([...inputFields, 
  //     // { firstName: '', 
  //     // lastName: '', 
  //     // volunteer_userID: '', 
  //     // beneficiary_beneficiaryID:'' 
  //     // },
